CREATE OR REPLACE TABLE config_facts AS
SELECT
    column1 AS fact_id,
    column2 AS name,
    column3 AS description,
    PARSE_JSON(column4) AS group_info,
    column5 AS is_master_data,
    CAST(column6 AS STRING) AS deployed,
    column7 AS deployment_error,
    column8 AS bridge_pattern,
    PARSE_JSON(column9) AS source_details,
    PARSE_JSON(column10) AS attributes,
    PARSE_JSON(column11) AS also_required_for,
    PARSE_JSON(column12) AS edges,
    PARSE_JSON(column13) AS join_keys,
    column14 AS created_at,
    column15 AS pii,
